package String;
import java.io.*;
import java.util.*;

public class Short {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 4;
		String names[]= { "Rahul", "Ajay", "Gourav", "Riya" };
		Arrays.sort(names);
		System.out.println("The names in alphabetical order are: ");
		for (int i = 0; i < n; i++) 
		 {
		System.out.println(names[i]);
		 }


	}

}
